import {Button} from 'react-native-elements'
import React from 'react';
import {View, Text, TextInput, StyleSheet, Picker, SafeAreaView, ScrollView} from 'react-native';

const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: 'column',
        justifyContent: 'space-around',
        alignItems: 'stretch',
        margin: 20
    },
    textInput: { height: 40,
        borderColor: 'gray',
        borderWidth: 1
    },
    warningfield: {
        color: "red"
    },
    picker: {
        height: 80
    },
    buttonRegister: {
        backgroundColor: "#32a852",
        width: "100%"
    }
  });

class RegisterView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            form: {
                name: null,
                surname: null,
                password: null,
                confirmPassword: null,
                experience: null,
                email: null,
                country : null
            },
            correctMessage : "hello"
        };
    }
    handleChange(name, value) {
        this.state.form[name] = value;
        console.log(this.state.form);
    }

   onSubmit = () => {
        console.log("hello")
        var fetchForbidden = false;
        if(this.state.form.name === null){
            this.state.correctMessage += "Name cannot be null. ";
            console.log(1);
            fetchForbidden = true;
        }
        if(this.state.form.surname === null){
            this.state.correctMessage += "Surname cannot be null. ";
            fetchForbidden = true;
            console.log(2);
        }
        if(this.state.form.password === null){
            this.state.correctMessage += "Password cannot be null. "
            fetchForbidden = true;
            console.log(3);
        }
        if(this.state.form.confirmPassword === null){
            this.state.correctMessage += "Confirm password cannot be null. "
            fetchForbidden = true;
            console.log(4);
        }
        if(this.state.form.email === null){
            this.state.correctMessage += "Email cannot be null. "
            fetchForbidden = true;
            console.log(4);
        }
        if(this.state.form.experience === null){
            this.state.correctMessage += "Experience cannot be null. "
            fetchForbidden = true;
            console.log(5);
        }
        if(this.state.form.country === null){
            this.state.correctMessage += "Country cannot be null. "
            fetchForbidden = true;
            console.log(6);
        }
        if(this.state.form.password !== this.state.form.confirmPassword){
            this.state.correctMessage += "Confirm password has to be same as password. "
            fetchForbidden = true;
            console.log(7);
        }
        this.setState({correctMessage: this.state.correctMessage})
        if(fetchForbidden === false)
        {
            this.state.correctMessage = "";
            fetch('https://yachmanservice.azurewebsites.net/api/Account/Register', {
                method: 'post',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    Email: this.state.form.email,
                    Password: this.state.form.password,
                    ConfirmPassword: this.state.form.confirmPassword
                })
            }).then((res) => res.json())
            .then((data) =>console.log(data))
                .catch((err)=>console.log(err));

                fetch('https://yachmanservice.azurewebsites.net/api/AppUsers', {
                    method: 'post',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body:JSON.stringify({
                        Email: this.state.form.email,
                        Country: this.state.form.country,
                        Name: this.state.form.name,
                        Surname: this.state.form.surname,
                        Experience: this.state.form.experience
                    })
                })
                .then((res) => res.json())
                .then((data) => {
                    alert("Registered " + data.Name);
                })
                .catch((err)=>console.log(err));
        }
    }
    render(){
        return (
            <SafeAreaView style={styles.container}>
                <ScrollView>
                    <View>
                        <Text>Email</Text>
                        <TextInput style={styles.textInput} onChangeText={(txt) => this.handleChange("email", txt)}/>
                    </View>
                    <View>
                        <Text>Name</Text>
                        <TextInput style={styles.textInput} onChangeText={(txt) => this.handleChange("name", txt)}/>
                    </View>
                    <View>
                        <Text>Surname</Text>
                        <TextInput style={styles.textInput} onChangeText={(txt) => this.handleChange("surname", txt)}/>
                    </View>
                    <View>
                    <Text>Country</Text>
                        <Picker onValueChange={(txt) => this.handleChange("country", txt)}>
                            <Picker.Item label="Poland" value="Poland" />
                            <Picker.Item label="Germany" value="Germany" />
                            <Picker.Item label="Great Britain" value="Great Britain" />
                            <Picker.Item label="USA" value="USA" />
                            <Picker.Item label="Russia" value="Russia" />
                            <Picker.Item label="Brasil" value="Brasil" />
                        </Picker>
                    </View>
                    <View>
                        <Text>Experience</Text>
                        <Picker onValueChange={(txt) => this.handleChange("experience", txt)}>
                            <Picker.Item label="Novice" value="1" />
                            <Picker.Item label="Intermediate" value="2" />
                            <Picker.Item label="Advanced" value="3" />
                            <Picker.Item label="Expert" value="4" />
                        </Picker>
                    </View>
                    <View>
                        <Text>Password</Text>
                        <TextInput textContentType="password" autoCompleteType="password" style={styles.textInput} onChangeText={(txt) => this.handleChange("password", txt)}/>
                        <Text>Confirm Password</Text>
                        <TextInput textContentType="password" autoCompleteType="password" style={styles.textInput} onChangeText={(txt) => this.handleChange("confirmPassword", txt)}/>
                    </View>
                    <View>
                        <Text>{this.state.correctMessage} hello</Text>
                    </View>
                    <Button buttonStyle ={styles.buttonRegister} title="Register" onPress={this.onSubmit}></Button>
                </ScrollView>
            </SafeAreaView>
        );
    }
}
export default RegisterView;
